function isItemNumber(form){
	if (Number(form.query.value)){
		if ((Number(form.query.value) > '99999999') && (Number(form.query.value) < '2147483647')){
			window.location = 'http://cgi.v3.qa.ebay.com/ws/eBayISAPI.dll?MfcISAPICommand=ViewItem&item=' + form.query.value;
		}
		else{
			form.submit();
		}
	}
	else{
		form.submit();
	}
}
