function slideshowupdate(imgName,controls) 
				{ 
					if (play == 1)	
					{ 
						slidesslideshow = new Image(37,149); 
						slidesslideshow.src = testurl[pictures]; 
						slideshow = eval(imgName + "slideshow.src");	
						document[imgName].src = slideshow;	
						pictures=pictures + 1 
						if (pictures > totalpictures) 
							pictures=1 
						if (controls != 1) 
							timedelay(); 
						return; 
					} 
					timedelay(); 
					return; 
				} 
				function timedelay() 
				{ 
					timerID = setTimeout("slideshowupdate('slides',0)",2000); 
				} 
				function cplay() 
				{ 
					play=1 
				} 
				function cbegin() 
				{ 
					if (play == 0) 
					{ 
						play=1 
						pictures=1 
						slideshowupdate('slides') 
						play=0 
						return; 
					} 
					pictures=1 
					slideshowupdate('slides',1) 
				} 
				function cprev() 
				{ 
					if (play == 0) 
					{ 
						play=1 
						if (pictures < 3) 
							pictures=pictures+totalpictures 
						pictures=pictures-2 
						slideshowupdate('slides',1) 
						play=0 
						return;	
					} 
					if (pictures < 3) 
						pictures=pictures+totalpictures 
					pictures=pictures-2 
					slideshowupdate('slides',1) 
				} 
				function cnext() 
				{ 
					if (play == 0) 
					{ 
						play=1 
						slideshowupdate('slides',1) 
						play=0 
						return; 
					} 
					slideshowupdate('slides',1) 
				} 
				function cend()					
				{									
					if (play == 0)					
					{								
						play=1						
						pictures=totalpictures		
						slideshowupdate('slides',1)	
						play=0						
						return;						
					}								
					pictures=totalpictures			
					slideshowupdate('slides',1)		
				}									

				function cstop()					
				{									
					play=0							
				}									

				function cpause()					
				{									
					if (pause == 0)					
					{								
						pause=1						
						pauseon()					
						return;						
					}								
					if (pause == 1)					
					{								
						pause=0						
						pauseon()					
						return;						
					}								
				}									

				function pauseon()				
				{									
					if (pause == 1)					
					{								
						play = 0					
						return;						
					}								
					play = 1						
				}									



//

timedelay();
document.write('<a href="javascript: cprev()"><img src="http://pics.ebay.com/aw/pics/ipixSlideshowPrev.gif" border="0" width="55" height="50"></a><a href="javascript: cstop()"><img src="http://pics.ebay.com/aw/pics/ipixSlideshowStop.gif" border="0" width="55" height="50"></a><a href="javascript: cplay()"><img src="http://pics.ebay.com/aw/pics/ipixSlideshowPlay.gif" border="0" width="55" height="50"></a><a href="javascript: cnext()"><img src="http://pics.ebay.com/aw/pics/ipixSlideshowNext.gif" border="0" width="55" height="50"></a>');


